mapper.select:
select count(*)
from volunteer_record
where activity_id =#{activityId,jdbcType=INTEGER}

mapper.insert:
insert into volunteer_record (id, volunteer_id, activity_id, 
  status, volunteer_evaluate, worker_evaluate
  )
values (#{id,jdbcType=INTEGER}, #{volunteerId,jdbcType=INTEGER}, #{activityId,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{volunteerEvaluate,jdbcType=VARCHAR}, #{workerEvaluate,jdbcType=REAL}
  )
